import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function GameDetails() {

        let { gameId } = useParams(); 
       
    
        let [gameObject, setGame] = useState({});
       
       
        async function getGamedeatails(id) {
       
         let { data } = await axios.get(`https://free-to-play-games-database.p.rapidapi.com/api/games/`+id+`?rapidapi-key=b52128808dmsh5826403ec30ac21p1b9548jsnfca5769e0b68`);
         console.log(data);
    
         setGame(data);
       
        }
       
       
       
        useEffect(() => {
       
            getGamedeatails(gameId);
        }, [])
       
       
       
        return (
         <div className='container py-5'>
          <div className="row">
           <div className='col-md-4'>
            <div>
             <img src={`${gameObject.thumbnail}`} className="w-100 rounded-2" alt="" />
            </div>
            <div className="row">
                <div className="col-3 col-lg-2 me-2">
                <span  className="btn btndark mb-3 py-2 cursor">FREE</span>
                </div>
                <div className="col me-0 pe-0">
                <span  className="btn btnblue w-100 py-2 me-0"><strong>PLAY NOW </strong><i class="fas fa-sign-out-alt"></i></span>
                </div>
            </div>
           </div>
           <div className="col-md-8">
            <h1>{gameObject.title}</h1>
            <h2>About {gameObject.title}</h2>
            <p>{gameObject.description}</p>

            <h1>Minimum System Requirements</h1>
            <h3>graphics : {gameObject.graphics}</h3>
            <h3>memory : {gameObject.memory}</h3>
            <h3>os : {gameObject.os}</h3>
            <h3>processor : {gameObject.processor}</h3>
            <h3>storage : {gameObject.storage}</h3>

            <h1>Genshin Impact Screenshots</h1>
            <img src={'${gameObject.image}'} alt="" />


            <h1>Additional Information</h1>
            <div className="row">
                <div className="col-md-4">
                    <h3>Title</h3>
                    <h3></h3>
                </div>
            </div>
           </div>
          </div>
       
         </div>
        );
       }
