import React, { useState, useEffect } from 'react';
import { NavLink, useParams } from 'react-router-dom';
import axios from 'axios';

export default function GameDetails() {

    let { gameId } = useParams();


    let [gameObject, setGame] = useState({});


    async function getGamedeatails(id) {

        let { data } = await axios.get(`https://free-to-play-games-database.p.rapidapi.com/api/game`, {
            params: { id },
            headers: {
                'X-RapidAPI-Key': 'b52128808dmsh5826403ec30ac21p1b9548jsnfca5769e0b68',
                'X-RapidAPI-Host': 'free-to-play-games-database.p.rapidapi.com'
            }
        }, gameObject);

        // let {data} = await axios.get(`https://free-to-play-games-database.p.rapidapi.com/api/game`+id+
        // `?rapidapi-key=b52128808dmsh5826403ec30ac21p1b9548jsnfca5769e0b68`,gameObject)

        setGame(data);


        console.log(data);
        // console.log(gameObject.minimum_system_requirements);



    }


    useEffect(() => {

        getGamedeatails(gameId);

    }, [])



    return (
        <div className='container py-5'>
            <div className="row mt-3">
                <div className='col-md-4'>
                    <div>
                        <img src={`${gameObject.thumbnail} `} className="w-100 rounded-2" alt="" />
                    </div>
                    <div className="row mt-3">
                        <div className="col-3 col-lg-2 me-2">
                            <span className="btn btndark mb-3 py-2 cursor">FREE</span>
                        </div>
                        <div className="col-9 me-0 pe-0">

                            <NavLink onClick={() => window.open(`https://www.freetogame.com/${gameObject.title}`)}>
                                <span className="btn btnblue w-100 py-2  me-0"><strong>PLAY NOW </strong><i className="fas fa-sign-out-alt"></i></span>
                            </NavLink>
                        </div>
                    </div>
                </div>
                <div className="col-md-8">
                    <h1>{gameObject.title}</h1>
                    <h5 className='mt-3'>About {gameObject.title}</h5>
                    <p className='mt-3'>{gameObject.description}</p>
                    <h5 className='mt-3'>Minimum System Requirements</h5>
                    {/* <p><strong>graphics:</strong>{gameObject.minimum_system_requirements.graphics}</p> */}
                    {/* <ul className="ms-2 list-unstyled">
                        <li><strong>graphics :</strong>{gameObject.minimum_system_requirements.graphics}</li>
                        <li><strong>memory :</strong>{gameObject.minimum_system_requirements.memory}</li>
                        <li><strong>os :</strong>{gameObject.minimum_system_requirements.os}</li>
                        <li><strong>processor :</strong>{gameObject.minimum_system_requirements.processor}</li>
                        <li><strong>storage :</strong>{gameObject.minimum_system_requirements.storage}</li>
                    </ul> */}

                    <h1>Genshin Impact Screenshots</h1>
                    <img src={'${gameObject.image}'} alt="" />


                    <h2 className='mb-3'>Additional Information</h2>
                    <div className="row mb-3">
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Title</span>
                            <p>{gameObject.title}</p>
                        </div>
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Developer</span>
                            <p>{gameObject.developer}</p>
                        </div>
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Publisher</span>
                            <p>{gameObject.publisher}</p>
                        </div>
                    </div>
                    <div className="row">
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Release Date</span>
                            <p>{gameObject.release_date}</p>
                        </div>
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Genre</span>
                            <p>{gameObject.genre}</p>
                        </div>
                        <div className="col-md-4 col-6">
                            <span className='text-muted'>Platform</span>
                            <p><i className="fab fa-windows me-1"></i>{gameObject.platform}</p>
                        </div>
                    </div>
                </div>
            </div>

        </div>
    );
}
